    /**
     * 考察子序列的最后两项
     *
     * 从左到右遍历 nums，遍历的同时，维护（实时更新）一个二维数组 f[y][x]，表示对于目前已经遍历过的数，最后两项为 y 和 x 的子序列的最长长度。
     *
     * 对于 x=nums[i]，我们可以在「最后两项为 x 和 y 的子序列」的末尾添加 x，得到末尾为 x,y,x 的子序列，只看最后两个数，就是「最后两项为 y 和 x 的子序列」，因为添加了一个元素，子序列的长度增加 1，即
     *
     * f[y][x]=f[x][y]+1
     *
     * @param nums
     * @param k
     * @return
     */
    public int maximumLength6(int[] nums, int k) {
        int ans = 0;
        int[][] f = new int[k][k];
        for (int x : nums) {
            x %= k;
            for (int y = 0; y < k; y++) {
                f[y][x] = f[x][y] + 1;
                ans = Math.max(ans, f[y][x]);
            }
        }
        return ans;
    }

    public int maximumLength5(int[] nums, int k){
        int[][] array = new int[k][k];
        int[] mapKey = new int[k];
        List<Integer> mapVal = new ArrayList<>(nums.length);
        int result = 0;
        int mod;
        int preMod = -1;

        for (int num: nums) {
            mod = num % k;

            if (mod == preMod) {
                array[mod][mod] ++;
                result = Math.max(result, array[mod][mod]);
                continue;
            }

           if (mapKey[mod] == 0) {
               mapKey[mod] = 1;
               mapVal.add(mod);
           }

           for (int m: mapVal) {
               // 遍历每个朝向，维护朝向的最大长度
               if (m == mod) {
                   array[mod][m] ++;
               }else if (array[m][mod] == 0){
                   array[mod][m] = 2;
               }else {
                   array[mod][m] = array[m][mod] + 1;
               }
               result = Math.max(result, array[mod][m]);
           }

            preMod = mod;
        }

        return result;
    }


    public int maximumLength4(int[] nums, int k){
        int[][] array = new int[k][k];
        int[] tmpArray;
        int result = 0;
        int i;
        int mod;
        int next;
        for (int num: nums) {
            mod = num % k;
            for (i = 0; i < k; i++) {
                tmpArray = array[i];
                next = (k + i - mod) % k;

                if (tmpArray[mod] == 0 && tmpArray[next] != 0) {
                    continue;
                }

                tmpArray[mod] ++;
                result = Math.max(result, tmpArray[mod]);

                if (mod == next) {
                    continue;
                }

                tmpArray[next] = Math.max(tmpArray[next], tmpArray[mod]);

                tmpArray[mod] = 0;
            }
        }

        return result;
    }


    public int maximumLength3(int[] nums, int k){
        List<Integer>[][] list = new ArrayList[k][k];
        int i;
        for (i = 0; i < k; i++) {
            list[i] = new ArrayList[k];
        }
        List<Integer>[] tmpList;
        i = 0;
        int mod;
        int next;
        for (int num: nums) {
            for (i = 0; i < k; i++) {
                tmpList = list[i];
                mod = num % k;
                next = (k + i - mod) % k;

                if (tmpList[mod] == null && tmpList[next] != null) {
                    continue;
                }

                if (tmpList[mod] == null) {
                    tmpList[mod] = new ArrayList<>();
                }

                tmpList[mod].add(num);

                if (mod == next) {
                    continue;
                }

                if (tmpList[next] == null) {
                    tmpList[next] = tmpList[mod];
                }else if (tmpList[mod].size() > tmpList[next].size()){
                    tmpList[next] = tmpList[mod];
                }

                tmpList[mod] = null;
            }
        }

        int result = 0;
        for (List<Integer>[] l1: list) {
            for (List<Integer> l2: l1) {
                if (l2 == null) {continue;}
                result = Math.max(result, l2.size());
            }
        }

        return result;
    }


    public int maximumLength(int[] nums, int k) {

        List<Integer>[][] list = new ArrayList[k][k];
        int i;
        List<Integer>[] tmpList;
        for (int num: nums) {
            for (i = 0; i < k; i++) {
                tmpList = list[i];
                for (List<Integer> tmpList2: tmpList) {
                    if (tmpList2 == null) {continue;}
                    if ((tmpList2.get(tmpList2.size() - 1) + num) % k == i) {
                        tmpList2.add(num);
                    }
                }

                int tmp = num % k;
                if (tmpList[tmp] == null) {
                    tmpList[tmp] = new ArrayList<>();
                    tmpList[tmp].add(num);
                }
            }

        }

        int result = 0;

        for (List<Integer>[] l1: list) {
            for (List<Integer> l2: l1) {
                if (l2 == null) {continue;}
                result = Math.max(result, l2.size());
            }
        }

        return result;
    }


    public int maximumLength2(int[] nums, int k) {

        List<List<Integer>>[] list = new ArrayList[k];
        int i;
        List<List<Integer>> tmpList;
        for (int num: nums) {
            outer:
            for (i = 0; i < k; i++) {
                tmpList = list[i];
                if (tmpList == null) {
                    list[i] = new ArrayList<>();
                    list[i].add(new ArrayList<>());
                    list[i].get(0).add(num);
                    continue;
                }

                for (List<Integer> tmpList2: tmpList) {
                    if ((tmpList2.get(tmpList2.size() - 1) + num) % k == i) {
                        tmpList2.add(num);
                        // 这里真的可以跳过吗?
                        continue outer;
                    }
                    if (tmpList2.get(0) % k == num % k) {
                        continue outer;
                    }
                }

                List<Integer> tmp2 = new ArrayList<>();
                tmp2.add(num);
                tmpList.add(tmp2);
            }

        }

        int result = 0;

        for (List<List<Integer>> l1: list) {
            for (List<Integer> l2: l1) {
                result = Math.max(result, l2.size());
            }
        }

        return result;
    }

